    CalendarEditDialog.prototype.colorConvert = function(hex, brightness = 0) {
        let r = 0;
        let g = 0;
        let b = 0;

        if (hex.includes('#')) {
            // Convert hex to RGB first
            if (hex.length === 4) {
                r = '0x' + hex[1] + hex[1];
                g = '0x' + hex[2] + hex[2];
                b = '0x' + hex[3] + hex[3];
            } else if (hex.length === 7) {
                r = '0x' + hex[1] + hex[2];
                g = '0x' + hex[3] + hex[4];
                b = '0x' + hex[5] + hex[6];
            }
        }else{
            // Parse RGB
            let rgb = hex.split('(')[1].split(')')[0].split(',').splice(0,3);
            r = rgb[0];
            g = rgb[1];
            b = rgb[2];
        }
        r /= 255;
        g /= 255;
        b /= 255;

        let cmin = Math.min(r,g,b);
        let cmax = Math.max(r,g,b);
        let delta = cmax - cmin;
        let h = 0;
        let s = 0;
        let l = 0;

        if (delta === 0) {
            h = 0;
        }else if (cmax === r) {
            h = ((g - b) / delta) % 6;
        }else if (cmax == g) {
            h = (b - r) / delta + 2;
        }else {
            h = (r - g) / delta + 4;
        }

        h = Math.round(h * 60);

        if (h < 0) {
            h += 360;
        }

        l = (cmax + cmin) / 2;
        s = delta == 0 ? 0 : delta / (1 - Math.abs(2 * l - 1));
        s = +(s * 100).toFixed();
        l = +(l * 100).toFixed();

        if (brightness !== 0) {
            let ratio = 20;

            if (l >= 50) {
                l = l - ratio;
            }

            if (brightness > 0) {
                l = l + parseInt(brightness, 10);
                if (l >= 100) {
                    l = l - 10
                }
            }else if(brightness < 0) {
                l = l + parseInt(brightness, 10)
            }
        }

        let result_obj = {
            hsl: `hsl(${h},${s}%,${l}%)`
        }

        // convert hsl to hex
        l /= 100;
        const a = s * Math.min(l, 1 - l) / 100;
        const f = n => {
            const k = (n + h / 30) % 12;
            const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
            return Math.round(255 * color).toString(16).padStart(2, '0');   // convert to Hex and prefix "0" if needed
        };

        result_obj.hex = `#${f(0)}${f(8)}${f(4)}`;

        return result_obj;
    }

